var expect = require('chai').expect;
var jsEmpty = require('../js-empty');

describe('jsEmpty test', function(){
    it('check all params #1', function(){
        var params = {
            name:'MrTam',
            email:null
        };
        expect(jsEmpty(params)).to.be.true;
    });
    it('check and skip params #1', function(){
        var params = {
            name:'MrTam',
            email:null
        };
        expect(jsEmpty(params, ['email'])).to.be.false;
    });
    it('check all params #2', function(){
        var params = {
            name:'MrTam',
            email:null,
            birthday: {
                day: 10,
                month: 8,
                year: null
            }
        };
        expect(jsEmpty(params, ['email'])).to.be.true;
    });
    it('check and skip params #2', function(){
        var params = {
            name:'MrTam',
            email:null,
            birthday: {
                day: 10,
                month: 8,
                year: null
            }
        };
        expect(jsEmpty(params, {'email':'email', birthday:['year']})).to.be.false;
    });
    it('check and skip params #3', function(){
        var params = {
            name:'MrTam',
            email:null,
            birthday: {
                day: 10,
                month: 8,
                year: null
            }
        };
        expect(jsEmpty(params, ['email', 'year'])).to.be.false;
    });
});

